 pools(where:{address:$address})
 {
 id
 address
 }
}
", "expression": "function(data) {
 if (data == null || data.pools == null) {
 return 0
 } else if (Array.isArray(data.pools) && data.pools.length == 0) {
 return 0
 }
 return 1
}" } } }}
